function loadData(data_urls){
  for (var i = 0; i < data_urls.length; i++){
    var data_url = data_urls[i];
    $.ajax({
      type: "GET",  
      url: data_url,
      dataType: "text",       
      success: function(response)  
      {
        fullData = fullData.concat($.csv.toArrays(response));
        ordering = [...Array(fullData.length).keys()];
        randomizeOrdering();
        updateCardData();
      }   
    });

  }
}



$.urlParam = function(name, null_value){
    var results = new RegExp('[\?&]' + name + '=([^&#]*)').exec(window.location.href);
    if (results==null){
       return null_value;
    }
    else{
       return results[1] || 0;
    }
}


function nextCard(){
	idx = (idx + 1) % fullData.length;
	chime();
	updateCardData();
	return false;
}

function previousCard(){
	idx = ((idx - 1) + fullData.length) % fullData.length;  // because javascript doesn't handle negative mod
	updateCardData();
	return false;
}


function shuffleArrayInPlace(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
}

function shuffleArrayWithSeed(array, seed) {
    let currentIndex = array.length, temporaryValue, randomIndex;
    seed = seed || 1;
    let random = function() {
      var x = Math.sin(seed++) * 10000;
      return x - Math.floor(x);
    };
    // While there remain elements to shuffle...
    while (0 !== currentIndex) {
      // Pick a remaining element...
      randomIndex = Math.floor(random() * currentIndex);
      currentIndex -= 1;
      // And swap it with the current element.
      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }
    return array;
}

function randomizeOrdering(){
	ordering = shuffleArrayWithSeed(ordering, seed);
}

function updateCardData(){
	currentCardId = ordering[idx]
	$('#card-number').html(currentCardId)
	sample = fullData[currentCardId]
	$("#key-word").html(sample[0])
	
	var taboo_words = sample[1].split(",")
	shuffleArrayInPlace(taboo_words)
	
	var taboo_words_holders = $('#taboos-div').children('p')
	for (var i = 0; i < taboo_words_holders.length; i++){
		taboo_words_holders[i].innerHTML = taboo_words[i]
	}

	//update param in URL
	window.history.pushState('page2', 'Title', '?seed=' + seed + '&idx=' + idx);
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

function woosh() {
	var snd = new Audio('woosh.mp3');
	snd.play();
}

function chime() {
	var snd = new Audio('ping.wav');
	snd.play();
}

var timerValue = 60
function startCountdown(){
	timerValue -= 1;
	$('#timerValue').html(timerValue)
	$('#timerButton').attr('disabled', 'disabled')
	$('#timerButton').addClass('btn-info')
	$('#timerButton').removeClass('btn-danger')

	if (timerValue == 0) {
		$('#timerButton').removeClass('btn-info')
		$('#timerButton').addClass('btn-danger')
		$('#timerButton').removeAttr('disabled')
		beep(); setTimeout(beep, 300);
		$('#card-div').css('background-color', '#dc3545'); 
		setTimeout(function() { $('#card-div').css('background-color', '#17a2b8') }, 1000);
		timerValue = 60
	} else {
		setTimeout(startCountdown, 1000);

	}
	return false;
}

// Bindings

$('#timerButton').click(startCountdown)
$('#nextButton').click(nextCard)

$('#card-div').on("swiperight",function(){
  previousCard()
});

$('#card-div').on("swipeleft",function(){
  nextCard()
});



$(document).keydown(function(e) {
    switch(e.which) {
        case 37: // left
        	previousCard();
        	return;
        case 39: // right
        	nextCard();
        	return;
        default: return; // exit this handler for other keys
    }
    e.preventDefault(); // prevent the default action (scroll / move caret)
});

// Initialization

var fullData = [];
var ordering;
var currentCardId;
var seed = parseInt($.urlParam('seed', 0));
var idx = parseInt($.urlParam('idx', 0));

var data_urls = [
  "https://abidlabs.github.io/infinite-taboo/out_data/data1.csv",
  "https://abidlabs.github.io/infinite-taboo/out_data/data2.csv",
  "https://abidlabs.github.io/infinite-taboo/out_data/data3.csv",
]
loadData(data_urls);
